import React, {useState} from 'react'

const Login = () => {
    const [usernameValue, setUsernameValue] = useState('')
    const [passwordValue, setPasswordValue] = useState('')
    let apiToken
    const BASE_URL = "https://sandpit-api.safetyculture.io"

    const handleUsernameChange = (event) => {
        setUsernameValue(event.target.value)
    }

    const handlePasswordChange = (event) => {
        setPasswordValue(event.target.value)
    }

    const handleClick = () => {
        // setUsernameValue("")
        // setPasswordValue("")
        let url = BASE_URL + "/auth"
        fetch(url, {
            body: `grant_type=password&username=${usernameValue}&password=${passwordValue}`,
            headers: {
                "Content-Type": "application/x-www-form-urlencoded"
            },
            method: "POST"
          })
          .then(response => {
                apiToken = response.access_token
                console.log(response)
                // alert("Token Aquired")
          })
          .catch(error => console.log(error))
        //     url, {
        //     method: "POST",
        //     username: usernameValue,
        //     password: passwordValue,
        //     grant_type: 'password',
        // }, {
        //     headers: {
        //         "Content-Type": "application/x-www-form-urlencoded"
        //     },

        // })  .then(response => {
        //     // apiToken = response.access_token
        //     console.log(response)
        //     // alert("Token Aquired")
        // })  .catch(error => console.log(error.response.request._response))
    }

    const hasToken = apiToken !== undefined

    return(
        <div>
            <label value={usernameValue} onChange={handleUsernameChange}>Enter username: </label>
            <input type="text"/> 
            <br/>
            <label value={passwordValue} onChange={handlePasswordChange}>Enter password: </label>
            <input type="password"/>
            <br/>
            <button onClick={handleClick}>Get Token</button>
            <br/>
            <p>{hasToken ? apiToken: "no token"}</p>
            <p>{usernameValue}</p>
            <p>{passwordValue}</p>
        </div>
    )

}

export default Login
